import React, { useEffect, useState } from "react";
import Header from "../../pagecomps/Header";
import "../index.css";
import axios from "axios";

const ReturnBook = () => {
  const [values, setValues] = useState({});
  const [issued, setIssued] = useState([]);

  //submitHandler
  const submitHandler = (e) => {
    e.preventDefault();
    const { name, value } = e.target;
    setValues({ ...values });
  };

  //fetch the details from issue model during page load
  useEffect(() => {
    axios
      .get("http://localhost:3001/api/users/issuedlist")
      .then((res) => setIssued(res.data));
  }, []);

  //create loop for issued books
  if (!issued) return null;
  const issuedBooks = issued.map((data) => {
    console.log(data);

    //Date converter
    const convertDate = (data) => {
      const date = new Date(data);
      return date.toLocaleDateString();
    };

    //days calculator
    const totalDays = (idata) => {
      let tdata = new Date().toLocaleDateString();
      return tdata;
    };
    return (
      <tr>
        <td>
          {data.client.firstname} {data.client.lastname}
        </td>
        <td>{data.book.name}</td>
        <td>{convertDate(data.createdAt)}</td>
        <td>{totalDays(data.createdAt)}</td>
      </tr>
    );
  });

  return (
    <>
      <Header />
      <div className="contents_container">
        <table className="allbooks">
          <thead>
            <tr>
              <td>Client</td>
              <td>Book Issued</td>
              <td>Issued Date</td>
              <td>Total Days</td>
              <td>Action</td>
            </tr>
          </thead>
          <tbody>
            {issuedBooks}
            {/* <td>
                <form onSubmit={submitHandler}>
                  <input type="hidden" value="test" name="client" />
                  <input type="hidden" value="test" name="book" />
                  <button type="submit">Return Book</button>
                </form>
              </td> */}
          </tbody>
        </table>
      </div>
    </>
  );
};

export default ReturnBook;
