import React from "react";
import "./index.css";
import Header from "../pagecomps/Header";
import { useState } from "react";
import axios from "axios";

const AddClient = () => {
  const [values, setValues] = useState({
    firstname: "",
    lastname: "",
    email: "",
    mobile: "",
    userType: "",
  });

  const message_s = document.querySelector("#message_s");
  const handleChange = (e) => {
    const { name, value } = e.target;
    setValues({ ...values, [name]: value });
    console.log(values);
  };

  const handleSubmission = (e) => {
    e.preventDefault();

    const saveuser = () => {
      axios
        .post("http://localhost:3001/api/users/addclient", values)
        .then((res) => {
          message_s.innerHTML = res.message;
        });
    };

    saveuser();
  };
  return (
    <>
      <Header />
      <div className="contents_container">
        <form className="input_form" onSubmit={handleSubmission} id="adduser">
          <h1>Add a new Client</h1>
          <input
            id="firstname"
            type="text"
            name="firstname"
            placeholder="FirstName"
            value={values.firstname}
            onChange={handleChange}
          />
          <input
            id="lastname"
            type="text"
            name="lastname"
            placeholder="Last Name"
            value={values.lastname}
            onChange={handleChange}
          />
          <input
            id="email"
            type="email"
            name="email"
            placeholder="Email"
            value={values.email}
            onChange={handleChange}
          />
          <input
            id="mobile"
            type="mobile"
            name="mobile"
            placeholder="Mobile"
            value={values.mobile}
            onChange={handleChange}
          />
          <select onChange={handleChange} value={values.userType}>
            <option value="student">Student</option>
            <option valaue="staff">Staff</option>
          </select>

          <button type="submit">Save Client</button>
          <span id="message_s"></span>
        </form>
      </div>
    </>
  );
};

export default AddClient;
