import React, { useEffect, useState } from "react";
import Header from "../../pagecomps/Header";
import "../index.css";
import axios from "axios";

const IssueBook = () => {
  //useState Const
  const [values, setValues] = useState({});
  const [clients, setClients] = useState();
  const [books, setBooks] = useState();

  //handle input change
  const handleChange = (e) => {
    const { name, value } = e.target;
    setValues({ ...values, [name]: value });
  };

  //Fetch all clients on page load
  useEffect(() => {
    const allClients = axios
      .get("http://localhost:3001/api/users/listclients")
      .then((res) => {
        return setClients(res.data);
      });
  }, []);

  //Fetch all books with atleast 1 qty left
  useEffect(() => {
    const allBooks = axios
      .get("http://localhost:3001/api/users/listbooks")
      .then((res) => {
        return setBooks(res.data.data);
      });
  }, []);

  //function to create options for clients
  if (!clients) return null;
  const clientList = clients.map((client) => {
    return (
      <option
        value={client._id}
      >{`${client.firstname} ${client.lastname}`}</option>
    );
  });

  //function to create option for books
  if (!books) return null;
  const bookList = books.map((book) => {
    return <option value={book._id}>{book.name}</option>;
  });

  //Handle Submission
  const submitHandler = (e) => {
    e.preventDefault();
    const saveIssue = axios.post("http://localhost:3001/api/users/issuebook/");
  };
  return (
    <>
      <Header />
      <div className="contents_container">
        <form className="input_form" id="addbook" onSubmit={submitHandler}>
          <select
            required
            name="client"
            id="client"
            value={values.client}
            onChange={handleChange}
          >
            {clientList}
          </select>
          <select
            required
            name="book"
            id="book"
            value={values.book}
            onChange={handleChange}
          >
            {bookList}
          </select>
          <button type="submit">Issue Book</button>
        </form>
      </div>
    </>
  );
};

export default IssueBook;
