import React, { useEffect, useState } from "react";
import Header from "../../pagecomps/Header";
import "../index.css";

const ReturnBook = () => {
  const [values, setValues] = useState({});

  //submitHandler
  const submitHandler = (e) => {
    e.preventDefault();
    const { name, value } = e.target;
    setValues({ ...values });
  };

  //fetch the details from issue model during page load
  useEffect(() => {
    axios.get("http://localhost:3001/api/users/issuedlist");
  }, []);
  return (
    <>
      <Header />
      <div className="contents_container">
        <table className="allbooks">
          <thead>
            <tr>
              <td>Client</td>
              <td>Book Issued</td>
              <td>Issued Date</td>
              <td>Total Days</td>
              <td>Action</td>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td>Denny</td>
              <td>HP1</td>
              <td>29-4-1991</td>
              <td>5</td>
              <td>
                <form onSubmit={submitHandler}>
                  <input type="hidden" value="test" name="client" />
                  <input type="hidden" value="test" name="book" />
                  <button type="submit">Return Book</button>
                </form>
              </td>
            </tr>
          </tbody>
        </table>
      </div>
    </>
  );
};

export default ReturnBook;
