import React, { useEffect, useState } from "react";
import Header from "../../pagecomps/Header";
import "../index.css";
import axios from "axios";

const IssueBook = () => {
  //useState Const
  const [values, setValues] = useState({});
  const [clients, setClients] = useState();
  const [books, setBooks] = useState();

  //handle input change
  const handleChange = (e) => {
    const { name, value } = e.target;
    setValues({ ...values, [name]: value });
  };

  //Fetch all clients on page load
  useEffect(() => {
    const allClients = axios
      .get("http://localhost:3001/api/users/listclients")
      .then((res) => {
        console.log(res);
      });
  }, []);
  return (
    <>
      <Header />
      <div className="contents_container">
        <form className="input_form" id="addbook">
          <select
            required
            name="client"
            id="client"
            value={values.client}
            onChange={handleChange}
          >
            <option value="client1">Client1</option>
            <option value="client2">Client2</option>
            <option value="client3">Client3</option>
          </select>
          <select
            required
            name="book"
            id="book"
            value={values.book}
            onChange={handleChange}
          >
            <option value="book1">book1</option>
            <option value="book2">book2</option>
            <option value="book3">book3</option>
          </select>
          <button type="submit">Issue Book</button>
        </form>
      </div>
    </>
  );
};

export default IssueBook;
