import React, { useEffect, useState } from "react";
import Header from "../../pagecomps/Header";
import "../index.css";
import axios from "axios";

const AddBook = () => {
  //useState consts
  const [values, setValues] = useState({});
  const [messages, setMessages] = useState({});
  const [categories, setCategories] = useState({});

  //Handle input change
  const handleChange = (e) => {
    const { name, value } = e.target;
    setValues({ ...values, [name]: value });
  };
  //Submission Handler
  const handleSubmission = () => {};

  //Fetch category list from Books Model
  useEffect(() => {
    try {
      //   const cats = axios.get("http://localhost:3001/api/users/getcategories");
    } catch (err) {
      console.log(err);
    }
  }, []);

  return (
    <>
      <Header />
      <div className="contents_container">
        <form className="input_form" onSubmit={handleSubmission} id="addbook">
          <h1>Add a new book</h1>
          <input
            required
            type="text"
            id="name"
            name="name"
            placeholder="Enter Book Name"
            value={values.name}
            onChange={handleChange}
          />
          <input
            required
            type="text"
            id="author"
            name="author"
            placeholder="Enter Author Name"
            value={values.author}
            onChange={handleChange}
          />
          <input
            required
            type="text"
            id="description"
            name="description"
            placeholder="Enter Description"
            value={values.description}
            onChange={handleChange}
          />
          <input
            required
            type="text"
            id="isbn"
            name="isbn"
            placeholder="Enter ISBN"
            value={values.isbn}
            onChange={handleChange}
          />
          <input
            required
            type="text"
            id="cat"
            name="cat"
            placeholder="Enter Category"
            value={values.cat}
            onChange={handleChange}
          />
          <input
            required
            type="text"
            id="copies"
            name="copies"
            placeholder="Enter Qty"
            value={values.copies}
            onChange={handleChange}
          />
          <button type="submit">Save Book</button>
          <span id="message_s">{messages}</span>
        </form>
      </div>
    </>
  );
};

export default AddBook;
