    firstname: "",
    lastname: "",
    email: "",
    mobile: "",
    userType: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
  };
  return (
    <>
      <Header />
      <div className="contents_container">
        <form>
          <input
            type="text"
            name="firstname"
            value={values.firstname}
            onChange={handleChange}
          />
        </form>
      </div>
    </>
  );
};

export default AddClient;
